package stderr

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestStripStderr_LockfileVersionMismatchWarning(t *testing.T) {
	vector := "npm WARN read-shrinkwrap This version of npm is compatible with lockfileVersion@1, but package-lock.json was generated for lockfileVersion@2. I'll try to do my best with it!\n/srv/foobar/releases/20220420102847/frontend"

	_, output, err := Strip(vector)

	assert.Nil(t, err)
	assert.Equal(t, 0, len(output.Errors))
	assert.Equal(t, 0, len(output.Lines))
	assert.Equal(t, 1, len(output.Warnings))
}

func TestStripStderr_SomeErrors(t *testing.T) {
	vector := "npm ERR! missing: test@1.5.8, required by foo/bar@13.1.1\nnpm ERR! missing: test2@2.3.1, required by @some/other@13.1.1\nnpm ERR! cb() never called\nnpm ERR! extraneous: foo/bar@2.0.0-beta.16 /srv/foobar/releases/20220420102847/frontend/node_modules/test/parent1\nnpm ERR! missing: @foo/bar@2.0.0-beta.13 /srv/foobar/releases/20220420102847/frontend/node_modules/test/parent\nnpm ERR! extraneous: foo/bar2@2.0.0-beta.13 /srv/foobar/releases/20220420102847/frontend/node_modules/test/parent2\nnpm ERR! code EACCES\nnpm ERR! syscall access\nnpm ERR! path /usr/local/lib\nnpm ERR! errno -13\nnpm ERR! code ELIFECYCLE\nnpm ERR! undefined postinstall: `test`\nnpm ERR! Error: failed to fetch from registry: supervisor\nnpm ERR!     at /usr/share/npm/lib/utils/npm-registry-client/get.js:139:12\nnpm ERR! Linux 4.4.0-47-generic\nnpm ERR! argv \"/usr/local/bin/node\" \"/usr/local/bin/npm\" \"i\"\nnpm ERR! node v6.9.1\nnpm ERR! npm  v3.10.8\nnpm ERR! path /usr/local/lib/node_modules/npm/lib/fetch-package-metadata.js\nnpm ERR! Error: EACCES: permission denied, access '/usr/lib/node_modules'\nnpm ERR!  { Error: EACCES: permission denied, access '/usr/lib/node_modules'\nnpm ERR!   stack: 'Error: EACCES: permission denied, access \\'/usr/lib/node_modules\\'',\nnpm ERR!   errno: -13,\nnpm ERR!   code: 'EACCES',\nnpm ERR!   syscall: 'access',\nnpm ERR!   path: '/usr/lib/node_modules' }\nnpm ERR!\nnpm ERR! The operation was rejected by your operating system.\nnpm ERR! It is likely you do not have the permissions to access this file as the current user\nnpm ERR!\nnpm ERR! If you believe this might be a permissions issue, please double-check the\nnpm ERR! permissions of the file and its containing directories, or try running\nnpm ERR! the command again as root/Administrator (though this is not recommended).\n\nnpm ERR! A complete log of this run can be found in:\nnpm ERR!     /home/vagrant/.npm/_logs/2019-04-10T01_54_11_323Z-debug.log\n"

	strippedInput, output, err := Strip(vector)

	assert.Nil(t, err)
	assert.Equal(t, "", strippedInput)
	assert.Equal(t, 5, len(output.Errors))
	assert.Equal(t, 30, len(output.Lines))
	assert.Equal(t, 0, len(output.Warnings))

	item := output.Errors[0]
	assert.Equal(t, "test", item.Name)
	assert.Equal(t, "missing", item.Problem)
	assert.Equal(t, "1.5.8", item.Version)

	item = output.Errors[1]
	assert.Equal(t, "test2", item.Name)
	assert.Equal(t, "missing: test2@2.3.1, required by @some/other@13.1.1", item.Text)
	assert.Equal(t, "missing", item.Problem)
	assert.Equal(t, "2.3.1", item.Version)

	item = output.Errors[2]
	assert.Equal(t, "foo/bar", item.Name)
	assert.Equal(t, "extraneous: foo/bar@2.0.0-beta.16 /srv/foobar/releases/20220420102847/frontend/node_modules/test/parent1", item.Text)
	assert.Equal(t, "extraneous", item.Problem)
	assert.Equal(t, "2.0.0-beta.16", item.Version)

	item = output.Errors[3]
	assert.Equal(t, "@foo/bar", item.Name)
	assert.Equal(t, "missing", item.Problem)
	assert.Equal(t, "2.0.0-beta.13", item.Version)

	item = output.Errors[4]
	assert.Equal(t, "foo/bar2", item.Name)
	assert.Equal(t, "extraneous", item.Problem)
	assert.Equal(t, "2.0.0-beta.13", item.Version)
	assert.Equal(t, "/srv/foobar/releases/20220420102847/frontend/node_modules/test/parent2", item.Location)
}

func TestStripStderr_UnknownProblemKind(t *testing.T) {
	vector := "\nnpm ERR! covfefe: name@version\n"

	strippedInput, output, err := Strip(vector)

	assert.NotNil(t, err)
	assert.Equal(t, "", strippedInput)
	assert.Contains(t, err.Error(), "unknown npm error kind")
	assert.Equal(t, 0, len(output.Errors))
	assert.Equal(t, 1, len(output.Lines))
}

